  render() {
    const { data } = this.state;
    const isLoggedIn = this.state.isLoggedIn;
    return (
      <div>
        <SearchBar onClick={this.fetchUsers} />
        {isLoggedIn ? <List data={data} /> : null}
      </div>
    );
  }
}

ReactDOM.render(<App />, document.getElementById('root'));
